-- ----------------------------
-- Function structure for `IsSpace`
-- ----------------------------
DROP FUNCTION IF EXISTS `IsSpace`;
DELIMITER ;;
CREATE DEFINER=`bantak`@`%` FUNCTION `IsSpace`(sIn varchar(1024)) RETURNS varchar(1024) CHARSET utf8
BEGIN
	DECLARE run varchar(1024) DEFAULT 0;
	DECLARE temp varchar(1024) DEFAULT 0;
	SET temp = TRIM(sIn);
	if LENGTH(temp) = 0 THEN
		SET run = 0;
	else
		SET run =temp;
	end IF;
	RETURN run;
END
;;
DELIMITER ;
