INSERT INTO `risk_management` VALUES ('802', 'ผู้ป่วย HN 826 มารับบริการที่OPD วันที่ 7/7/57 ด้วยเรื่องอาการปวดหลังร้าวลงขาและเอว ขณะรอรับยาที่ห้องยาผู้ป่วย มีผื่นแดงตามผิวหนัง จึงเดินมาที่หน้าห้องตรวจแจ้งพยาบาลว่ามีอาการคันตามผิวหนังน่าจะแพ้ยาแล้วล้มลง ไม่รู้สึกตัว พยาบาลคลำชีพจรไม่ได้จึงนำส่งER ', '8', '1', '2', '5', '2014-07-09 00:00:00', '2014-07-09 15:55:04', '1', '');
