INSERT INTO `risk_management` VALUES ('808', 'ผู้ป่วย HN 74281 ในเวรบ่าย ถูกสุนัขกัดมา ER ให้สิทธิ์ชำระเงินเอง คนไข้แจ้งว่ามีบัตรทองแต่ไม่ได้เอามา และเป็นนอกเขต คนไข้ทราบว่าถ้าถูกสุนัขกัดสามารถใช้สิทธิ์บัตรทอง ฉุกเฉินได้ (ญาติแจ้งว่าศึกษาระบบมาแล้ว) ห้องยาจึงโทรแจ้งไป ER ให้เช็คสิทธิ์อีกครั้ง แต่ ER แจ้งว่าเช็คแล้ว ขึ้นเป็นชำระเงินเอง คนไข้จึงต้องชำระเงิน 590 บาท  ตอนเช้าวันต่อมาห้องยาลองให้ห้องประกันสุขภาพตรวจสอบ พบว่าเป็นบัตรทอง และสามารถใช้สิทธิ์ฉุกเฉินได้', '3', '7', '6', '4', '2014-07-17 00:00:00', '2014-07-18 09:29:57', '1', '');
