INSERT INTO `risk_management` VALUES ('1343', 'จัดยาไม่ตรงกับฉลาก\nผู้ป่วยนอก จัดยาเมทาโดนผิดขนาด เมื่อเอาวางไว้รอจ่าย มีคนทัก คนจัดจึงนึกได้ว่าจัดผิด จึงได้ไปเตรียม+จัดใหม่ ถ้าไม่มีคนทักหรือคนจัดจำไม่ได้ก็จะหลุดไป เพราะในขั้นตอนการจ่าย ผู้จ่ายไม่ทราบเลยว่าถูกความแรงหรือไม่ เนื่องจากขวดยา เมทาโดนทุกขวด มีสี ปริมาตรเท่ากันหมด ไม่มีตัวจำแนกให้ทราบว่ายาที่รอจ่ายเป็นความแรงอะไร จะมีเพียงคนเตรียม+จัดเท่านั้นที่ทราบ หากจัด+เตรียมทีเดียวหลายๆเคส และมีการสลับตะกร้ากับสติ๊กเกอรืกัน ก็จะจ่ายยาให้ผู้ป่วยผิดได้ โดยที่คนจ่ายไม่ทราบ ตรวจเช็คไม่ได้เลย จนกว่าผู้ป่วยจะเกิดพิาจากเมทาโดน\nวิธีแก้ไข : ควรปรับปรุงระบบการเตรียม + จัด + จ่ายเมทาโดนให้มีประสิทธิภาพมากขึ้น เพื่อให้เกิดระบบ double check ในการจ่ายยาเมทาโดน ให้ผู้เช็คยาสามารถจำแนกได้ว่าเมทาโดนที่จ่ายมีความแรงเท่าไร เพราะเมื่อจ่ายยาเมทาโดนที่มีผู้ป่วยมารับครั้งละหลายๆคนในทีเดียว โอกาสหลุดมีสูง  อาจทำได้โดยระบุความแรงไว้ที่ขวด หรือทำให้เห็นความแตกต่างของปริมาตรยา เช่น 20 mg, 20 ml, 30 mg, 30 ml\n', '2', '7', '5', '5', '2015-02-26 00:00:00', '2015-03-06 21:06:47', '1', '');
