INSERT INTO `risk_management` VALUES ('1525', 'อื่นๆ\nผู้ป่วยนอก เจ้าหน้าผู้ปฏิบัติงานสับสนใน WI การจ่ายยาเมทาโดน ขั้นตอนที่แจ้งให้ทราบไม่ตรงกัน เมื่อผู้รับผิดชอบไม่อยู่ผู้ที่ต้องปฏิบัติงานแทนไม่ทราบ WI ตามที่ควรจะเป็น เมื่อมีเคสที่ต้องจ่ายก็มักจะเกิดความวุ่นวายตลอด กระบวนการทำงานไม่ราบรื่นอย่างที่ควรจะเป็น ทั้งในส่วนที่อยู่ในฝ่ายเองและส่วนที่ต้องประสานงานนอกฝ่าย \nสาเหตุ WI ขาดความชัดเจน ไม่มีประสิทธิภาพ ไม่มีการประสานงานแจ้งข้อมูลเรื่อง Wi ให้ผู้ปฏิบัติทราบทั้ง บุคลากรในฝ่าย นอกฝ่าย ข้อมูล WI ไม่เป็นปัจจุบัน เจ้าหน้าที่ในฝ่ายเข้าใจไม่ตรงกัน \nวิธีแก้ไข ควรประสานแจ้งข้อมูล WI ที่เป็นปัจจุบัน ในการจ่ายเมทาโดนให้เจ้าหน้าที่เกี่ยวข้องทราบอย่างทั่วถึง ไม่ควรให้ผู้ที่รับผิดชอบรู้อยู่คนเดียว ควรวิเคราะห์และจัดทำ WI ให้ครอบคลุมและมีมาตรการบริหารความเสี่ยงที่รัดกุมกว่านี้ เพราะที่ผ่านมามักจะเกิดปัญหาก่อนแล้วค่อยมีการป้องกันแบบวัวหายแล้วล้อมคอกเสมอ', '2', '7', '5', '5', '2015-04-07 00:00:00', '2015-04-15 17:06:43', '1', '');
