INSERT INTO `risk_responsible` VALUES ('1767', 'ปกติยาหม่องไพลจะไม่แปะฉลากเพราะฉลากใหญ่กว่าขวด  ส่วนใหญ่แพทย์จะสั่งจ่าย 1 ขวด \nผู้จ่ายต้องไม่อาศัยความเคยชิน ต้องดูฉลากให้ละเอียดทุกครั้ง', '870', '49', '2014-08-21 17:35:58', '2014-08-21 17:35:58');
